export const base = 'http://localhost:5000';

export function reloadPrices (setData, setIsLoaded, setError) {
    fetch(`${base}/compare/btc`, {
      method: 'GET'
    })
    .then(response => response.json())
    .then(data => {
      setData(data);
      setIsLoaded(true);
      console.log(data);
    })
    .catch((error) => {
      setIsLoaded(true);
      setError(error);
    });
  }

export const cryptoCurrency = [
    {
      'name': 'Bitcoin',
      'abbr': 'btc',
    },
    {
      'name': 'Ethereum',
      'abbr': 'eth',
    }
  ]
