import React, {useState, useEffect} from 'react';
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

import {base} from './common/APIUtils';
import './App.css';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const App = (props) => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState([]);
  const [currency, setCurrency] = React.useState('btc');

  const cryptoCurrency = [
    {
      'name': 'Bitcoin',
      'abbr': 'btc',
    },
    {
      'name': 'Ethereum',
      'abbr': 'eth',
    }
  ]

  const handleCryptoChange = (event) => {
    setCurrency(event.target.value);
    reloadPrices();
    console.log('hellos')
  };

  function reloadPrices () {
    fetch(`${base}/compare/${currency}`, {
      method: 'GET'
    })
    .then(response => response.json())
    .then(data => {
      setData(data);
      setIsLoaded(true);
      console.log(data);
    })
    .catch((error) => {
      setIsLoaded(true);
      setError(error);
    });
  }

  useEffect(() => {
    reloadPrices();
  }, []);

  if (error) {
    return <div>{error.message}</div>;
  } else if (!isLoaded) {
    return <div></div>;
  } else {
    return (
      <div className="App">
        <header className="App-header">
          <h1>
            Compare Crypto Exchanges
          </h1>
          <Box>
            <FormControl fullWidth>
              <InputLabel>Crypto Currency</InputLabel>
              <Select
                value={currency}
                label="Crypto Currency"
                onChange={handleCryptoChange}
              >
              {cryptoCurrency.map((data) => (
                <MenuItem value={data.abbr}>{data.name}</MenuItem>
              ))}
              </Select>
            </FormControl>
          </Box>
          <p>
            Buy: {data['buy']} Sell: {data['sell']}
          </p>
          <Button onClick={reloadPrices}>Reload Prices</Button>
          <Grid container spacing={2} style={{height: '300px'}}>
            <Grid item xs={6} md={6}>
              <Item style={{height: '75%'}}>
                <h2>
                  Coinbase
                </h2>
                <div>
                  Buy Price: {data['coinbase']['buy']}
                </div>
                <div>
                  Sell Price: {data['coinbase']['sell']}
                </div>
              </Item>
            </Grid>
            <Grid item xs={6} md={6}>
              <Item style={{height: '75%'}}>
                <h2>
                  Crypto.com
                </h2>
                <div>
                  Buy Price: {data['crypto']['buy']}
                </div>
                <div>
                  Sell Price: {data['crypto']['sell']}
                </div>
              </Item>
            </Grid>
          </Grid>
        </header>
      </div>
    );
  }
}

export default App;
