const base = 'http://127.0.0.1:5000';

export async function getCoinbasePrice(currency) {
    var response = await fetch(`${base}/coinbase/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors',
      })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
        return data;
      })
      .catch((error) => {
        console.error('Error:', error);
        return error;
      });
}

export async function getCryptoPrice(currency) {
    var response = await fetch(`${base}/crypto/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors'
      })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
        return data;
      })
      .catch((error) => {
        console.error('Error:', error);
        return error;
      });
}
