const base = 'http://127.0.0.1:5000';

export function getCoinbasePrice(currency) {
    let response = fetch(`${base}/coinbase/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors'
      })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    //For Debugging
    console.log(response.data);

    return response;
}

export function getCryptoPrice(currency) {
    let response = fetch(`${base}/crypto/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors'
      })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    //For Debugging
    console.log(response.data);

    return response;
}
