const base = 'http://127.0.0.1:5000';

export async function getCoinbasePrice(currency) {
    let response = await fetch(`${base}/coinbase/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors',
      })
      .then(response => {return response.json()})
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    //For Debugging
    console.log(response);

    return response;
}

export async function getCryptoPrice(currency) {
    let response = await fetch(`${base}/crypto/${currency}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors'
      })
      .then(response => {return response.json()})
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    //For Debugging
    console.log(response);
}
