import React, {useState, useEffect} from 'react';
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';

import {base, reloadPrices} from './common/APIUtils';
import './App.css';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const App = (props) => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState([]);

  const cryptoCurrency = {
    'Bitcoin': 'btc',
    'Ethereum': 'eth'
  }

  useEffect(() => {
    reloadPrices(setData, setIsLoaded, setError);
  }, []);

  if (error) {
    return <div>{error.message}</div>;
  } else if (!isLoaded) {
    return <div></div>;
  } else {
    return (
      <div className="App">
        <header className="App-header">
          <h1>
            Compare Crypto Exchanges
          </h1>
          <p>
            Buy: {data['buy']} Sell: {data['sell']}
          </p>
          <Button onClick={reloadPrices(setData, setIsLoaded, setError)}>Reload Prices</Button>
          <Grid container spacing={2}>
            <Grid item xs={6} md={6}>
              <Item>
                Coinbase
                <div>
                  Buy Price: {data['coinbase']['buy']}
                </div>
                <div>
                  Sell Price: {data['coinbase']['sell']}
                </div>
              </Item>
            </Grid>
            <Grid item xs={6} md={6}>
              <Item>
                Crypto.com
                <div>
                  Buy Price: {data['crypto']['buy']}
                </div>
                <div>
                  Sell Price: {data['crypto']['sell']}
                </div>
              </Item>
            </Grid>
          </Grid>
        </header>
      </div>
    );
  }
}

export default App;
